#include <iostream>
#include <string>
#include <vector>
#include <unordered_map>
using namespace std;

/*
Idea
https://leetcode.wang/leetCode-30-Substring-with-Concatenation-of-All-Words.html
*/

class Solution {
public:
    vector<int> findSubstring(string s, vector<string>& words) {
        vector<int> ans;
        if(words.empty() || s.empty()) return ans;

        unordered_map<string, int> word_count;
        for(auto &word : words) {
            ++word_count[word];
        }

        int word_length = words[0].size();
        int num_words = words.size();

        for(int i = 0; i < word_length; ++i) {
            int start = i;
            int count = 0;
            unordered_map<string, int> curr_word_count;

            for(int j = i; j <= static_cast<int>(s.size()) - word_length; j += word_length) {
                string word = s.substr(j, word_length);
                if(word_count.count(word)) {
                    ++curr_word_count[word];
                    ++count;

                    while(curr_word_count[word] > word_count[word]) {
                        string left_word = s.substr(start, word_length);
                        --curr_word_count[left_word];
                        --count;
                        start += word_length;
                    }

                    if(count == num_words) {
                        ans.push_back(start);
                        string left_word = s.substr(start, word_length);
                        --curr_word_count[left_word];
                        --count;
                        start += word_length;
                    }
                }
                else {
                    curr_word_count.clear();
                    count = 0;
                    start = j + word_length;
                }
            }
        }

        return ans;
    }
};

int main() {
    string s = "";
    //s = "barfoofoobarthefoobarman";
    vector<string> words = {"ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba"};
    //words = {"bar","foo","the"};

    Solution sol = Solution();
    vector<int> ans = sol.findSubstring(s, words);
    for(int i=0; i<ans.size(); ++i) {
        cout << ans[i] << endl;
    }

    return 0;
}
